input = <<~INPUT
ULL
RRDDD
LURDL
UUUUD
INPUT

input = DATA.read

x, y = [1, 1]

keypad = [
  [1, 2, 3],
  [4, 5, 6],
  [7, 8, 9]
]

keypad2 = [
  [nil, nil,  1,  nil, nil],
  [nil,  2,   3,   4,  nil],
  [  5,  6,   7,   8,   9 ],
  [nil, "A", "B", "C", nil],
  [nil, nil, "D", nil, nil]
]

result = input.split("\n").map do |line|
  line.chars.each do |char|
    case char
    when "U"
      y -= 1 unless y == 0
    when "R"
      x += 1 unless x == 2
    when "D"
      y += 1 unless y == 2
    when "L"
      x -= 1 unless x == 0
    end
  end

  keypad[y][x]
end

puts "Part 1", result.join

x, y = [0, 2]

result = input.split("\n").map do |line|
  line.chars.each do |char|
    case char
    when "U"
      y -= 1 unless y == 0 || keypad2[y - 1][x].nil?
    when "R"
      x += 1 unless x == 4 || keypad2[y][x + 1].nil?
    when "D"
      y += 1 unless y == 4 || keypad2[y + 1][x].nil?
    when "L"
      x -= 1 unless x == 0 || keypad2[y][x - 1].nil?
    end
  end

  keypad2[y][x]
end

puts "Part 2", result.join

__END__





