INPUT

starmap = input.lines.map do |line|
  line = line.chomp.chars
  if line.include?("#")
    line
  else
    line.join.gsub(".", ",").chars
  end
end

starmap = starmap.transpose.map do |line|
  if line.include?("#")
    line
  else
    line.join.gsub(".", ",").chars
  end
end

galaxies = []
starmap.each_with_index do |line, y|
  line.each_with_index do |char, x|
    galaxies << [x, y] if char == "#"
  end
end

def calc(galaxies, starmap, offset:)
  galaxies.combination(2).reduce(0) do |distances, ((x1, y1), (x2, y2))|
    x1, x2 = [x1, x2].sort
    path = starmap[y1][x1..x2]

    y1, y2 = [y1, y2].sort
    y1.upto(y2) do |y|
      path << starmap[y][x2]
    end

    distances += path.count + offset * path.count(",") - 2
  end
end

puts "Part 1: #{calc(galaxies, starmap, offset: 1)}"
puts "Part 2: #{calc(galaxies, starmap, offset: 999_999)}"

starmap.each { puts _1.join.gsub("#", "\033[91m@\033[0m").gsub(".", "\033[30m.\033[0m").gsub(",", "\033[91m#\033[0m") }
